import React,{Component} from "react";

class StateComponent extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            name: "Cristian",
            apellido:"Ramirez"
         }
    }

    ChangeData(){
        alert("Estoy funcionando")
    }

    render() { 
        return ( 
            <>
            <h2>Mi nombre es: {this.state.name} {this.state.apellido}</h2>

            <button onClick={this.ChangeData}>Cambiar Datos</button>
            </>
         );
    }
}
 
export default StateComponent;
