import React,{useState} from 'react'

const Hooks = () => {
    const[color,setColor]=useState('yellow');

    const Bottom ={
        width:'100%',
        height:'1000px',
        backgroundColor:color
    }

    const changeColor=()=>{
        if(color)
            setColor('orangered');
        if(!color) 
            setColor('yellow');
    }

    return (
        <>
            <div style={Bottom}  className="container">
                <button onClick={changeColor}>Cambia color</button>
            </div>
            
        </>
    )
}

export default Hooks
